import React from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';

const CommonForm = ({ fetchActivities }) => {
  const { register, handleSubmit } = useForm();

  const onSubmit = async (data) => {
    try {
      await axios.post('http://localhost:5000/api/activities', data);
      alert('Activity registered successfully');
      // Fetch the updated activities after successful submission
      fetchActivities();
    } catch (error) {
      console.error('There was an error registering the activity!', error);
    }
  };

  return (
    <div className="container">
      <h2>Activity Registration</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-group">
          <label>Name</label>
          <input {...register('name')} className="form-control" />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input {...register('email')} className="form-control" />
        </div>
        <div className="form-group">
          <label>Role</label>
          <select {...register('role')} className="form-control">
            <option value="Student">Student</option>
            <option value="Faculty">Faculty</option>
          </select>
        </div>
        <div className="form-group">
          <label>Activity</label>
          <input {...register('activity')} className="form-control" />
        </div>
        <button type="submit" className="btn btn-primary">Register</button>
      </form>
    </div>
  );
};

export default CommonForm;
