import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie, Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const Dashboard = () => {
  const [faculties, setFaculties] = useState([]);
  const [activities, setActivities] = useState([]);

  // Fetch faculties and activities when the component mounts
  useEffect(() => {
    fetchFaculties();
    fetchActivities();
  }, []);

  // Fetch faculty data
  const fetchFaculties = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/faculty');
      setFaculties(response.data);
      console.log(response.data); // Debugging step to check the data
    } catch (error) {
      console.error('Error fetching faculties:', error);
    }
  };


  // Fetch activity data
  const fetchActivities = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/activities');
      setActivities(response.data);
    } catch (error) {
      console.error('Error fetching activities:', error);
    }
  };

  // Prepare data for the Faculty Department Pie Chart
  const departmentData = faculties.reduce((acc, faculty) => {
    acc[faculty.department] = (acc[faculty.department] || 0) + 1;
    return acc;
  }, {});

  // Prepare data for the Faculty Designation Pie Chart
  const designationData = faculties.reduce((acc, faculty) => {
    acc[faculty.designation] = (acc[faculty.designation] || 0) + 1;
    return acc;
  }, {});

  // Prepare data for the Faculty Gender Pie Chart
  const genderData = faculties.reduce((acc, faculty) => {
    acc[faculty.gender] = (acc[faculty.gender] || 0) + 1;
    return acc;
  }, {});

  // Prepare data for the Activity Pie Chart (Students vs Faculties)
  const studentCount = activities.filter(a => a.role === 'Student').length;
  const facultyCount = activities.filter(a => a.role === 'Faculty').length;

  // Prepare data for the Activity Bar Graph (Activity Types)
  const activityTypes = activities.reduce((acc, activity) => {
    acc[activity.activity] = (acc[activity.activity] || 0) + 1;
    return acc;
  }, {});

  return (
    <div>
      <h2>Faculty Management Dashboard</h2>

      {/* Faculty Department Pie Chart */}
      <h3>Faculty Department Distribution</h3>
      <Pie 
        data={{
          labels: Object.keys(departmentData),
          datasets: [{
            data: Object.values(departmentData),
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
          }],
        }}
      />

      {/* Faculty Designation Pie Chart */}
      <h3>Faculty Designation Distribution</h3>
      <Pie 
        data={{
          labels: Object.keys(designationData),
          datasets: [{
            data: Object.values(designationData),
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
          }],
        }}
      />

      {/* Faculty Gender Pie Chart */}
      <h3>Faculty Gender Distribution</h3>
      <Pie 
        data={{
          labels: Object.keys(genderData),
          datasets: [{
            data: Object.values(genderData),
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
          }],
        }}
      />

      {/* Activity Participation Pie Chart */}
      <h3>Activity Participation (Students vs Faculties)</h3>
      <Pie 
        data={{
          labels: ['Students', 'Faculties'],
          datasets: [{
            data: [studentCount, facultyCount],
            backgroundColor: ['#FF6384', '#36A2EB'],
          }],
        }}
      />

      {/* Activity Types Bar Graph */}
      <h3>Activity Types</h3>
      <Bar 
        data={{
          labels: Object.keys(activityTypes),
          datasets: [{
            label: 'Activity Count',
            data: Object.values(activityTypes),
            backgroundColor: 'rgba(75,192,192,1)',
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 2,
          }],
        }}
        options={{
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        }}
      />
    </div>
  );
};

export default Dashboard;
