import React, { useEffect, useState } from 'react';
import axios from 'axios';
import FacultyForm from './FacultyForm'; // Make sure to import your form components
import CommonForm from './CommonForm';

const Dashboard = () => {
  const [activities, setActivities] = useState([]);
  const [faculties, setFaculties] = useState([]);

  // Fetch data when the component mounts
  useEffect(() => {
    fetchActivities();
    fetchFaculties();
  }, []);

  // Function to fetch activities
  const fetchActivities = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/activities');
      setActivities(response.data);
    } catch (error) {
      console.error('Error fetching activities:', error);
    }
  };

  // Function to fetch faculties
  const fetchFaculties = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/faculty');
      setFaculties(response.data);
    } catch (error) {
      console.error('Error fetching faculties:', error);
    }
  };

  return (
    <div>
      <h2>Dashboard</h2>
      {/* Include the FacultyForm and pass fetchFaculties as prop */}
      <FacultyForm fetchFaculties={fetchFaculties} />
      {/* Include the CommonForm and pass fetchActivities as prop */}
      <CommonForm fetchActivities={fetchActivities} />

      <h3>Activities</h3>
      <ul>
        {activities.map(activity => (
          <li key={activity._id}>{activity.name} - {activity.activity}</li>
        ))}
      </ul>
      <h3>Faculties</h3>
      <ul>
        {faculties.map(faculty => (
          <li key={faculty._id}>{faculty.name} - {faculty.department}</li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
